// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;

import "./Section.sol";

contract Course {

    mapping(string => address) CoursesData;

    function InsertNewSectionsData(
        string calldata _courseKey,
        string calldata _sectionKey,
        string calldata _hash,
        string calldata _data) external
    {
        if( CoursesData[_courseKey] != address(0) )
        {
            Section sec = Section(CoursesData[_courseKey]);
            sec.SetData(_sectionKey, _hash, _data);
        }
        else{
            Section sec = new Section();
            sec.SetData(_sectionKey, _hash, _data);
            CoursesData[_courseKey] = address(sec);
        }
    }


    function GetHash(string calldata _courseKey, string calldata _sectionKey) external view returns (string memory)
    {
        if( CoursesData[_courseKey] != address(0) )
        {
           Section s = Section(CoursesData[_courseKey]);
           return s.GetHash(_sectionKey);
        }
        else{
            return "Record Not Found Against This Key";
        }
    }

    function GetMarksRecord(string calldata _courseKey, string calldata _sectionKey) external view returns (string memory)
    {
        if( CoursesData[_courseKey] != address(0) )
        {
           Section s = Section(CoursesData[_courseKey]);
           return s.GetMarksRecord(_sectionKey);
        }
        else{
            return "Record Not Found Against This Key";
        }
    }

}
